import React, { Component } from 'react';
import UserAdd from './components/UserAdd';
import Users from './components/Users';
import './App.css';

export default class App extends Component {

  state = {
    kategori: [{kategoryName:"Ayakkabı"},
    {kategoryName :"Elbise"}],
     current : ""
  }


show = (kategori) =>{
    this.setState(
      {current : kategori.kategoryName}
    )

}

  render() {
    const  kategori  = { title: "Kategori", title1: "product" }

    return (
      <div className="container w-75 m-auto">
        <h1 className='text-center'>My-Apps</h1>
        <div className=' d-flex justify-content-start align-item-center'>

          <Users kategori ={this.state.kategori} kategorid={kategori.title1} click={this.show}/>
          <UserAdd kategorid={kategori.title} /></div> 
      </div>
    );
  }
}
