import React, { Component } from 'react';

export default class UserAdd extends Component {
  state = {
    name: '',
    surname: '',
    email: ''
  };

  onChangeName = (e) => {
    this.setState({
      name: e.target.value
    });
  };

  onChangeSur = (e) => {
    this.setState({
      surname: e.target.value
    });
  };

  onChangeMail = (e) => {
    this.setState({
      email: e.target.value
    });
  };

  onAdd = (e) => {
    e.preventDefault();

    const { name, surname, email } = this.state;
    const { addUser } = this.props;

    const newUser = {
      id: Math.floor(Math.random()*100),
      name: name,
      surname: surname,
      email: email
    };

    addUser(newUser);
  };

  render() {
    const { name, surname, email } = this.state;

    return (
      <div>
        <form className='form' onSubmit={this.onAdd}>
          <div className='card p-5 w-75 m-auto '>
            <div className='mb-3'>
              <label htmlFor='name' className='form-label'>
                NAME:
              </label>
              <input
                type='text'
                placeholder='Ad覺n覺z'
                name='name'
                id='name'
                value=""
                className='form-control ms-3'
                onChange={this.onChangeName}
              />
            </div>

            <div>
              <label htmlFor='surname' className='form-label'>
                LAST-NAME:
              </label>
              <input
                type='text'
                placeholder='Soyad覺n覺z'
                name='surname'
                id='surname'
                value=""
                onChange={this.onChangeSur}
                className='form-control ms-3'
              />
            </div>

            <div className='mb-3'>
              <label htmlFor='email' className='form-label'>
                E-MAIL:
              </label>
              <input
                type='email'
                name='email'
                id='email'
                placeholder='E-mail giriniz'
                value=""
                onChange={this.onChangeMail}
                className='form-control ms-3'
              />
            </div>
            <button type='submit' className='btn btn-danger w-25'>
              Ekle
            </button>
          </div>
        </form>
      </div>
    );
  }
}
