        { id: 4, name: "Berat" , lastname:"balaban" , email :"Berat@gmail.com"}
    ]


}


  delete = (id) => {

   let update =  this.state.users;

    update = update.filter(user => user.id !==id);

   this.setState({    users:update
   })




  }





  render(){

    return(
        <div className="container">
        <h1 className='text-center'>User App</h1>
        <UserAdd click = {this.clickEvent}/>
