import React, { Component } from 'react';

export default class Users extends Component {
  
  state = {
    kategori: [{ id:1 , kategoryName:"Ayakkabı"},
    { id:2 ,kategoryName :"Elbise"}],
     current : ""
  }

  click = (kategori ) => {
    this.setState({
      current: kategori.kategoryName
    })
  }
    
  render() {
    
    return (
      <div className='w-25'>
        <h4>{this.props.kategorid}</h4>
        <div className="list">
          <ul>
          {
            this.kategori.map(kategori=> 
              <li key={kategori.id} className='pt-3' onClick={this.click(kategori)}>{kategori.kategoryName }</li>
              )
          }  <li>{this.state.current}</li>
           </ul>
        </div>
      </div>
    );
  }
}
