import React, { Component } from 'react'

export default class Us extends Component {
    state = {
        kategori: [
            { id: 1, name: "Ayakkabı" },
            { id: 1, name: "T-shirt" },
            { id: 1, name: "Pantolon" },
            { id: 1, name: "Sweat" }
        ],
        context : ""
    }

change = (kategori) => {
   this.setState({context : kategori.name})
}


    render() {
        return (
            <div className='user'>

                {
                    this.props.state.kategori.map(kategori => (
                        <div>
                            <ul><li key={kategori.id} onClick={()=>this.change(kategori)}> {kategori.name} </li></ul>
                        </div>

                   ) )
                }

                    <h1>{this.props.state.context}</h1>
            </div>
        )
    }
}
