import React, { Component, useState } from 'react'



export default class User extends Component {
  
 state = {
      categorys: [
        { id:1 , catogryNames :"dodo"},
        {id:2 , catogryNames :"melo"}],

        conTextt : ""
      };

    change = (cato) => {
     this.setState({ contextt : cato.catogryNames})
    };
  
  render() {
    
    return (
      <div className="user-list">

        <div className="catogry">
          {
             this.state.categorys.map(cato => (
                  <div onClick={() => this.change(cato)}  key={cato.id}>{cato.catogryNames}</div>

             ))

          }
          
        </div>
        <h4>{this.state.conTextt}</h4>
      </div>
    )
  }
}
