import React, { Component, useState } from 'react'
import Nav from "./components/Nav"
import Us from "./components/Us"
import './App.css';
export default class App extends Component{
  state = {
    kategori: [
        { id: 1, name: "Ayakkabı" },
        { id: 1, name: "T-shirt" },
        { id: 1, name: "Pantolon" },
        { id: 1, name: "Sweat" }
    ],
    context : ""
}

change = (kategori) => {
  this.setState({context : kategori.name})
  setInterval(this.setState.context = "",1000)

}

  render(){

    return(
        <div className="container">
         <h1>Logo</h1>
         <Us change ={this.change} kategori ={this.state.kategori} context ={this.state.context}/>
         <Nav context ={this.state.context} />


        </div>


    )
  }

}
