import React, { Component } from 'react'
import  PropTypes  from 'prop-types'
import { isVisible } from '@testing-library/user-event/dist/utils';


export default class user extends Component {
    constructor(props){
    super(props)
   
    

    this.state = {
       
      isVisible : false

    }

}
 
onClickEvent = () => {
    this.setState({
      isVisible : !this.state.isVisible
    });
   

}
  render() {
    const {name,surname,salary,departman} = this.props;
    const {isVisible} = this.state;
    return (
      <div className="user-list">
      <div className="container">
        <div className="name" onClick={this.onClickEvent}> <h1>{name} {surname}</h1></div>
       
       {isVisible ? 
        <div className="content">
          <div className="departman">departman : {departman}</div>
          <div className="salary">maaş: {salary}</div>
          
        </div>:null}
      </div>
      </div>
    )
  }
}

user.propTypes = {
  name: PropTypes.string.isRequired,
  salary: PropTypes.string.isRequired,
  departman: PropTypes.string.isRequired,
  surname: PropTypes.string.isRequired

}

user.defaultProps = {
  name: "kullanıcı bulunamadı",
  surname:"soy ad bulunumadı",
  salary: "Maaş bulunamadı",
  departman:"Departman bulunamadı"
}
