import React, { Component } from 'react';
import UserAdd from './components/UserAdd';
import Users from './components/Users';
import './App.css';

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
    users: [
      { id: 1, name: "Melis", lastname: "balaban", email: "melis@gmail.com" },
      { id: 2, name: "Doğukan", lastname: "balaban", email: "dodo@gmail.com" },
      { id: 3, name: "Memo", lastname: "balaban", email: "mehmet@gmail.com" },
      { id: 4, name: "Berat", lastname: "balaban", email: "Berat@gmail.com" }
    ]
  }
  }

  delete = (id) => {
    let updated = this.state.users;
    updated = updated.filter(user => user.id!== id);
    this.setState({
      users: updated
    });
  };

  addUser = (newUser) => {

    let update= this.state.users;
    update.push(newUser) ;

    this.setState({
      users : update
    });

  };



  render() {
    return (
      <div className="container">
        <h1 className='text-center'>User App</h1>
        <UserAdd addUser={this.addUser} />
        <Users kategori={this.state.users} delete={this.delete} />
      </div>
    );
  }
}
