        { id: 1, name: "Doğukan" ,email : "dodo@gmail.com"},
        { id: 1, name: "Memo",email : "mehmet@gmail.com"  },
        { id: 1, name: "Berat" ,email :"Berat@gmail.com"}
    ]

}





  render(){

    return(
        <div className="container">
        <h1 className='text-center'>User App</h1>
        <form action="">

          <div className="name">
            <label htmlFor="name">Name: </label>
            <input type="text" placeholder='adınız' name='name' />
            </div>
            <div className="email">
            <label htmlFor="email">E-mail: </label>
            <input type="email" name="email" id="email" placeholder='e-mail giriniz'/>
            </div>

        </form>
        </div>


    )
  }

}
