import React, { Component, useState } from 'react'

import './App.css';
export default class App extends Component{
  state = {
    kategori: [
        { id: 1, name: "Melis",email  : "melis@gmail.com" },
        { id: 1, name: "Doğukan" ,email : "dodo@gmail.com"},
        { id: 1, name: "Memo",email : "mehmet@gmail.com"  },
        { id: 1, name: "Berat" ,email :"Berat@gmail.com"}
    ]

}





  render(){

    return(
        <div className="container">
        <h1 className='text-center'>User App</h1>
        <form action="">
          <div className="card p-5 w-50">
          <div className="mb-3 d-flex">
            <label htmlFor="name" className='form-label'>Name: </label>
            <input type="text" placeholder='adınız' name='name' id='name' className='form-control w-25 ms-3'/>
            </div>

            <div className="mb-3 d-flex">
            <label htmlFor="email" className='form-label' >E-mail: </label>
            <input type="email" name="email" id="email" placeholder='e-mail giriniz' className='form-control w-25 ms-3'/>
            </div>
         </div>
        </form>
        </div>


    )
  }

}
