import React, { Component } from 'react'

export default class UserAdd extends Component {
 
  state={
    name :"",
    surname :"",
    email :""
  }

  onChangeName =(e) => {
    
    this.setState({
            [e.target.name] : e.target.value
    })
  }
  onChangeSur =(e) => {
    
    this.setState({
            [e.target.name] : e.target.value
    })
  }
  onChangeMail =(e) => {
    
    this.setState({
            [e.target.name] : e.target.value
    })
  }
  onAdd =(e) =>{
    
    this.props.UserAdd

    e.preventDefault()

  }
  
  render() {
    const {name,surname,email} = this.state;
    return (
      <div>
          <form action="" className='form' onSubmit={this.onAdd} >
          <div className="card p-5 w-75 m-auto d-flex aling-items-center justify-content-center">
          <div className="mb-3 ">
            <label htmlFor="name" className='form-label' > NAME: </label>
            <input type="text" placeholder='Adınız' name='name' id='name'value={name} className='form-control  ms-3' onChange={this.onChangeName}/>
            </div>
         
         <div>
         <label htmlFor="lname" className='form-label'> lAST-NAME: </label>
            <input type="text" placeholder='Soyadınız' name='surname' id='lname' value={surname} onChange={this.onChangeSur} className='form-control  ms-3'/>

         </div>
          

           
            <div className="mb-3">
            <label htmlFor="email" className='form-label' >E-MAİL: </label>
            <input type="email" name="email" id="email" placeholder='E-mail giriniz' value={email} onChange={this.onChangeMail} className='form-control  ms-3'/>
           
            </div> 
            <button type="submit" className='btn btn-danger w-25'  >Ekle</button>
         </div>
        </form>
      </div>
    )
  }
}
